// Editor.js
import React, { useEffect, useRef } from 'react';
import { EditorState, RangeSetBuilder } from '@codemirror/state';
import { EditorView, lineNumbers, gutter } from '@codemirror/view';
import { javascript } from '@codemirror/lang-javascript';
import { RedTextMarker } from './linemarker';

const Editor = ({ value, linesToMarkInRed }) => {
  const editorRef = useRef(null);

  useEffect(() => {
    if (!editorRef.current) return;

    const startState = EditorState.create({
      doc: value,
      extensions: [
        javascript(),
        gutter(),
        lineNumbers(),
        EditorView.decorations.of((view) => {
          const builder = new RangeSetBuilder();
          linesToMarkInRed.forEach(line => {
            const pos = view.state.doc.line(line).from;
            builder.add(pos, pos, new RedTextMarker());
          });
          return builder.finish();
        }),
      ],
    });

    const editorView = new EditorView({
      state: startState,
      parent: editorRef.current,
    });

    return () => editorView.destroy();
  }, [value, linesToMarkInRed]);

  return <div ref={editorRef} className="editor-container"></div>;
};

export default Editor;





