import React, { useState, useEffect, useCallback } from "react";
import Editor from "./editor";
import "./outline.css";
import { useCollapse } from "react-collapsed";
import Xarrow from 'react-xarrows';
import { Box } from "./box";

export function Outline(props) {
  // const [subfolderMap, setSubfolderMap] = useState([]);
  const subfolderMap = {
    1 : "Parse info into files",
    2 : "Format and Display Pics",
    3 : "Connect Doordash API",
    4 : "Read Menu to JSON",
    5 : "Format Footer",
    6 : ""
  };
  // setSubfolderMap(initSubfolderMap);

  const [files, setFiles] = useState([]);
  const [currentFile, setCurrentFile] = useState(null);
  const [outlineID, setOutlineID] = useState("");
  const [colorToUse, setColorToUse] = useState("");
  const [activeButton, setActiveButton] = useState("");  // Track active button
  const [showPopup, setShowPopup] = useState(false);     // Popup visibility state

  const [editedFolders, setEditedFolders] = useState([]);
  // Define folder structure and toggle functionality
  const [folders, setFolders] = useState([
    {
      name: "Home Page",
      children: [
        { name: "Parse info into files" },
        { name: "Format and Display Pics" },
      ],
      isOpen: false,
    },
    {
      name: "Order Online",
      children: [
        { name: "Connect Doordash API" },
        { name: "Read Menu to JSON" },
      ],
      isOpen: false,
    },
    {
      name: "Contact Us",
      children: [
        { name: "Format Footer" }
      ],
      isOpen: false,
    },
  ]
  );

  const toggleFolder = (folderName) => {
    setFolders(prevFolders =>
      prevFolders.map(folder =>
        folder.name === folderName ? { ...folder, isOpen: !folder.isOpen } : folder
      )
    );
  };

  // Fetch files when the component mounts
  useEffect(() => {
    async function fetchFiles() {
      try {
        const responses = await Promise.all([
          fetch('/example-code/apis.py'),
          fetch('/example-code/parse.py'),
          fetch('/example-code/format.py')
        ]);
        if (responses.every(response => response.ok)) {
          const [apiText, parseText, setupText] = await Promise.all(responses.map(response => response.text()));
          console.log(apiText);
          const newFiles = [
            {
              name: "apis.py",
              content: apiText,
              linesToColor: {
                "": [],
                "Parse info into files": [],
                "Format and Display Pics": [],
                "Connect Doordash API": [15, 95, 96, 97, 98, 99, 100, 101, 102, 103, 104, 105, 106, 107, 108, 109, 110, 111, 112, 113, 114],
                "Read Menu to JSON": [19, 20, 21, 22, 23],
                "Format Footer": []
              },
            },
            {
              name: "parse.py",
              content: parseText,
              linesToColor: { "": [],
                "Parse info into files": [],
                "Format and Display Pics": [],
                "Connect Doordash API": [],
                "Read Menu to JSON": [6, 7, 8, 9, 10, 11, 12],
                "Format Footer": []
              },
            },
            {
              name: "format.py",
              content: setupText,
              linesToColor: { "": [],
                "Parse info into files": [],
                "Format and Display Pics": [],
                "Connect Doordash API": [],
                "Read Menu to JSON": [6, 7, 8],
                "Format Footer": []
              },
            }
          ];
          setFiles(newFiles);
          setCurrentFile(newFiles[0]);
        } else {
          throw new Error('Failed to fetch one or more files');
        }
      } catch (error) {
        console.error("Error fetching files:", error);
      }
    }
    fetchFiles();
  }, []);

  const handleContentChange = useCallback(
    ({ name, content, linesToColor }) => {
      if (currentFile) {
        const updatedFiles = files.map(file => {
          if (file.name === currentFile.name) {
            console.log("Updating file:", file.name);
            return { ...file, content, ...(linesToColor && { linesToColor }) };
          }
          return file;
        });
        setFiles(updatedFiles);
        setCurrentFile(prev => ({
          ...prev,
          content,
          ...(linesToColor && { linesToColor })
        }));
      }
    },
    [currentFile, files]
  );

  const handleButtonClick = (buttonName) => {
    setActiveButton(buttonName);
  };

  const handleEditOutlineClick = () => {
    setShowPopup(true);
    setEditedFolders([...folders]); // Initialize with original folders
  };

  const handlePopupClose = () => {
    setShowPopup(false);
  };

  if (!currentFile) return <div>Loading files...</div>; 

  // Main App
  return (
    <div className="vscode-app">
      <main className="vscode-main">
        <section className="editor">
          <div className="editor-placeholder">
            <h1 className="editor-title">
              Outline
              <span className="material-symbols-outlined help">help</span>
            </h1>
            <FolderStructure
                folders={folders}
                onToggleFolder={toggleFolder}
            />
          </div>
            {/* Folder structure component */}
          <div className="edit-outline-container">
            {/* Popup Code */}
            <button
              className="primary-button edit-outline"
              onClick={handleEditOutlineClick}
            >
              Edit Outline
            </button>
          </div>
        </section>

        <aside className="terminal">
          <div className="editor-window">
            <div className="file-selector">
              {files.map((file) => {
                const is_highlighted =
                  file.linesToColor[outlineID].length !== 0;
                const is_hidden = outlineID !== "Read Menu to JSON" && file.name === "format.py";
                const buttonStyle = is_highlighted
                  ? { borderBottom: `8px solid ${colorToUse}` }
                  : {};
                const buttonClass = is_hidden
                  ? "filename-button-hidden"
                  : "filename-button";

                return (
                  <button
                    style={buttonStyle}
                    className={buttonClass}
                    key={file.name}
                    onClick={() => setCurrentFile(file)}
                  >
                    {file.name}
                  </button>
                );
              })}
            </div>
            <Editor
              linesToColor={currentFile.linesToColor[outlineID]}
              colorToUse={colorToUse}
              currentFile={currentFile}
              onContentChange={handleContentChange}
            />
          </div>
        </aside>
      </main>
      {showPopup && <Popup onClose={handlePopupClose} />}
    </div>
  );

  function FolderStructure({ folders, onToggleFolder }) {
    const [totalHeight, setTotalHeight] = useState(0);

    return (
      <div className="folder-structure">
        {folders.map((folder, index) => (
          <FolderItem
            key={folder.name}
            folder={folder}
            index={index}
            totalHeight={totalHeight}
            setTotalHeight={setTotalHeight}
            onToggleFolder={onToggleFolder}
            setOutlineID={setOutlineID} // Pass setOutlineID as a prop
            setColorToUse={setColorToUse} // Pass setColorToUse as a prop
          />
        ))}
      </div>
    );
  }

  function FolderItem({
    folder,
    index,
    totalHeight,
    setTotalHeight,
    onToggleFolder,
    setOutlineID,
    setColorToUse,
  }) {
    const { getCollapseProps, getToggleProps, isExpanded } = useCollapse({
      defaultExpanded: folder.isOpen,
    });

    const folderHeight = isExpanded ? 30 + folder.children.length * 20 : 30;
    const top = index === 0 ? 0 : totalHeight;

    const handleSubfolderClick = (e, subfolderName) => {
      e.preventDefault();
      e.stopPropagation();
      if (outlineID === subfolderName) {
        setOutlineID("");
      } else {
        setOutlineID(subfolderName)
      }
      console.log(subfolderName);
      if (subfolderName === subfolderMap[1]) {
        setColorToUse("#32D4CC");
      } else if (subfolderName === subfolderMap[2]) {
        setColorToUse("#FFEA99");
      } else if (subfolderName === subfolderMap[3]) {
        setColorToUse("#7F83D3");
      } else if (subfolderName === subfolderMap[4]) {
        setColorToUse("#D47F9D");
      } else if (subfolderName === subfolderMap[5]) {
        setColorToUse("#7FADD3");
      } else {
        setColorToUse("red");
      }
    };

    const [connections, setConnections] = useState([]);

    const handleClosePopup = () => {
      setShowPopup(false);
    };
    const [arrows, setArrows] = useState([]);
    const addArrow = ({ start, end }) => {
      setArrows([...arrows, { start, end }]);
    };

    return (
      <div className="folder-item" style={{ top }}>
        <div
          className="folder-toggle"
          onClick={() => onToggleFolder(folder.name)}
          // {...getToggleProps()}
        >
          <span
            className={`material-symbols-outlined ${
              isExpanded ? "folder-open" : "folder"
            }`}
          >
            chevron_right
          </span>
          <span className="folder-name">{folder.name}</span>
        </div>
        {folder.children && isExpanded && (
          <ul className="subfolders" {...getCollapseProps()}>
            {folder.children.map((subfolder) => (
              <li key={subfolder.name} className="subfolder-item">
              <div class="subfolder-box">
              <div class="subfolder-text" 
              onClick={(e) => handleSubfolderClick(e, subfolder.name)}
              style={{
                color: subfolder.name === outlineID ? colorToUse : 'white',
                fontWeight: subfolder.name === outlineID ? "bold" : "normal"
              }}>
                {subfolder.name}
              </div>
              <Box
                addArrow={addArrow}
                handler="right"
                boxId={subfolder.name}
              />
              </div>
              {arrows.map(ar => (
              <Xarrow
                start={ar.start}
                end={ar.end}
                key={ar.start + "-." + ar.end}
              />
              ))}
              </li>
            ))}
          </ul>
        )}
        {connections.map((conn, idx) => (
          <Xarrow
            key={idx}
            start={conn.start}
            end={conn.end}
            startAnchor="auto"
            endAnchor="auto"
            color="magenta"
          />
        ))}
      </div>
    );
  }


  function Popup({ onClose }) {
    const folders = [
      {
        name: "Bakery Website",
        children: [
          {
            name: "Home Page",
            children: [
              { name: "Parse info into files" },
              { name: "Format and Display Pics" },
            ],
          },
          {
            name: "Order Online",
            children: [
              { name: "Connect Doordash API" },
              { name: "Read Menu to JSON" },
            ],
          },
          {
            name: "Contact Us",
            children: [{ name: "Format Footer" }],
          },
        ],
      },
    ];

    const renderFolder = (folder) => (
      <div key={folder.name} className="folder">
        <span className="material-symbols-outlined">menu</span>
        <span className="folder-name">{folder.name}</span>
        {folder.children && (
          <ul className="subfolders">
            {folder.children.map((subfolder) => (
              <li key={subfolder.name} className="subfolder">
                <span className="material-symbols-outlined">menu</span>
                <span className="folder-name">{subfolder.name}</span>
                {subfolder.children && (
                  <ul className="sub-subfolders">
                    {subfolder.children.map((subSubfolder) => (
                      <li key={subSubfolder.name} className="sub-subfolder">
                        <span className="material-symbols-outlined">menu</span>
                        <span className="folder-name">{subSubfolder.name}</span>
                      </li>
                    ))}
                  </ul>
                )}
              </li>
            ))}
          </ul>
        )}
      </div>
    );

    return (
      <div className="popup">
        <div className="popup-header">
          <h2 className="popup-title">Bakery Website</h2>
          <button className="popup-close" onClick={onClose}>
            &times;
          </button>
        </div>
        <div className="popup-content">
          {folders.map((folder) => renderFolder(folder))}
        </div>
        <div className="popup-footer">
          <button className="popup-button cancel">Cancel</button>
          <button className="popup-button save">Save</button>
        </div>
      </div>
    );
  }
}
