// Editor.js
import React, { useEffect, useRef } from 'react';
import { EditorState } from '@codemirror/state';
import { EditorView, lineNumbers } from '@codemirror/view';
import { javascript } from '@codemirror/lang-javascript';

const Editor = ({ value, onChange, specialLineStyles }) => {
  const editorParentRef = useRef(null);

  useEffect(() => {
    const startState = EditorState.create({
      doc: value,
      extensions: [
        lineNumbers(),
        javascript(),
        EditorView.theme({
          // Apply default styles for line numbers
          '.cm-lineNumbers .cm-gutterElement': { color: 'black' },
          // Dynamic styling for specified line numbers using CSS variables
          ...Object.fromEntries(
            specialLineStyles.map((lineStyle, index) => [
              `.cm-lineNumber-${lineStyle.line} .cm-gutterElement`, 
              { color: lineStyle.color }
            ])
          ),
        }),
      ],
    });

    const view = new EditorView({
      state: startState,
      parent: editorParentRef.current,
    });

    return () => {
      view.destroy();
    };
  }, [value, onChange, specialLineStyles]);

  return <div ref={editorParentRef} />;
};

export default Editor;
