import React from 'react';
import Editor from './components/editor/editor';

function App() {
  const linesToColor = [2, 3, 4, 5, 6]; // Example lines to mark with a red gutter

  return (
    <div className="App">
      <h1>CodeMirror Editor with Custom Gutter Markers</h1>
      <Editor linesToColor={linesToColor} />
    </div>
  );
}

export default App;






