import React, { useState } from 'react';
import Editor from './components/editor/editor';
import './App.css'

function App() {
  const [files] = useState([
    { name: "example.js", content: "// JavaScript content" },
    { name: "example.py", content: "# Python content" },
    // Add more files as needed
  ]);

  // State to keep track of the currently selected file
  const [currentFileIndex, setCurrentFileIndex] = useState(0);

  const linesToColor = [2, 3, 4, 5, 6]; // Example lines to mark with a red gutter
  const colorToUse = "red"

  return (
    <div className="App">
      <div className="file-selector">
        {files.map((file, index) => (
          <button key={file.name} onClick={() => setCurrentFileIndex(index)}>
            {file.name}
          </button>
        ))}
      </div>
      <Editor linesToColor={linesToColor} colorToUse={colorToUse} />
    </div>
  );
}

export default App;






