import React, { useRef } from 'react';

const DraggablePoint = ({ id, onDragEnd, onDrop }) => {
  const ref = useRef(null);

  return (
    <div
      ref={ref}
      draggable
      onDragStart={(e) => {
        e.dataTransfer.setData("text/plain", id);  // Use "text/plain" for cross-browser compatibility
      }}
      onDragEnd={(e) => {
        const rect = ref.current.getBoundingClientRect();
        onDragEnd(id, { x: rect.left + window.scrollX, y: rect.top + window.scrollY });
      }}
      onDrop={(e) => {
        e.preventDefault();
        const draggedId = e.dataTransfer.getData("text/plain");
        const rect = ref.current.getBoundingClientRect();
        onDrop(draggedId, { x: rect.left + window.scrollX, y: rect.top + window.scrollY });
      }}
      style={{
        width: '20px',
        height: '20px',
        borderRadius: '50%',
        background: 'black',
        position: 'absolute',
        top: '50px', // Sample position
        left: `${50 + 100 * id}px` // Sample horizontal spacing
      }}
    />
  );
};

export default DraggablePoint;
