import React, { useState, useCallback } from 'react';
import Editor from './components/editor/editor';
import './App.css'

function App() {
  const [files, setFiles] = useState([
    { name: "example.js", content: "// JavaScript content" },
    { name: "example.py", content: "# Python content" },
    // Add more files as needed
  ]);

  // State to keep track of the currently selected file
  const [currentFileName, setCurrentFileName] = useState(files[0].name);

  const handleContentChange = useCallback((newContent) => {
    setFiles(currentFiles =>
      currentFiles.map(file =>
        file.name === currentFileName ? { ...file, content: newContent } : file
      )
    );
  }, [currentFileName]);

    // Find the current file based on the currentFileName, or default to an empty object
    const currentFile = files.find(file => file.name === currentFileName) || { name: '', content: '' };

  const linesToColor = [2, 3, 4, 5, 6]; // Example lines to mark with a red gutter
  const colorToUse = "red"

  return (
    <div className="App">
      <div className="file-selector">
        {files.map((file) => (
          <button key={file.name} onClick={() => setCurrentFileName(file.name)}>
            {file.name}
          </button>
        ))}
      </div>
      <Editor linesToColor={linesToColor} colorToUse={colorToUse} file={files.find((file) => file.name === currentFileName)}
        onContentChange={handleContentChange} />
    </div>
  );
}

export default App;






