import React, { useEffect, useRef } from 'react';
import { EditorState } from '@codemirror/state';
import { EditorView, gutter, GutterMarker, lineNumbers, Decoration, ViewPlugin } from '@codemirror/view';
import { basicSetup } from '@uiw/codemirror-extensions-basic-setup';
import { javascript } from '@codemirror/lang-javascript';
import { python } from '@codemirror/lang-python';

// Function to determine the language extension based on the file name
const getLanguageExtension = (fileName) => {
  if (fileName.endsWith('.js')) {
    return javascript();
  } else if (fileName.endsWith('.py')) {
    return python();
  }
  return [];
};

class ColoredGutterMarker extends GutterMarker {
  constructor(color) {
    super(); // Call the parent class constructor
    this.color = color; // Store the color value
  }
  toDOM() {
    const marker = document.createElement('div');
    marker.style.background = this.color;
    marker.style.width = '5px'; // Set the width of the block
    marker.style.height = '100%'
    return marker;
  }
}
// Editor component
const Editor = ({ linesToColor, colorToUse, currentFile, onContentChange }) => {
  const editorRef = useRef(null);
  // Define a plugin to manage the gutter markers
  const gutterMarkersPlugin = ViewPlugin.fromClass(class {
    constructor(view) {
        this.decorations = Decoration.none;
        this.update(view);
    }

    update(view) {
        // Assuming linesToColor is accessible in this scope; you might need to adjust this logic to access the current lines
        const decorations = linesToColor.map(lineNumber => {
            const pos = view.state.doc.line(lineNumber).from;
            return Decoration.widget({
                widget: new ColoredGutterMarker(colorToUse),
                side: -1
            }).range(pos);
        });
        this.decorations = Decoration.set(decorations);
    }
  }, {
    decorations: v => v.decorations
  });
  console.log("editor file: ", currentFile);
  useEffect(() => {
    if (!editorRef.current) return;
      // Define a function to create gutter markers based on linesToColor
      const createGutterMarkers = () => {
        return linesToColor.map(lineNumber => {
            const marker = new ColoredGutterMarker(colorToUse);
            // Assuming line numbers are 1-indexed and map directly to positions
            const pos = lineNumber - 1;
            return { pos, marker };
        });
      };
    // TODO: should we only update the actual content when we switch to other files??
    // TODO: actually, when we switch context states, when we switch back, only then create
    // a new editor and pull from the file contents?
    const updateListener = EditorView.updateListener.of((update) => {
      // we just want to write to the file contents here
      if (update.docChanged) {
        const newContent = update.state.doc.toString();
        console.log("new content:", newContent);
        onContentChange({name: currentFile.name, content: newContent});
      }
    });

    const langExtension = getLanguageExtension(currentFile.name);
    // Initialize editor state with custom gutter
    const startState = EditorState.create({
      doc: currentFile.content,
      extensions: [
        gutter(),
        // gutter({
        //   class: 'custom-gutter',
        //   lineMarker: (view, line) => {
        //     // Check if the current line number should have a marker
        //     const lineNumber = view.state.doc.lineAt(line.from).number;
        //     if (linesToColor.includes(lineNumber)) {
        //       return new ColoredGutterMarker(colorToUse);
        //     }
        //     return null; 
        //   },
        // }),
        lineNumbers(),
        basicSetup(),
        langExtension,
        updateListener,
        gutterMarkersPlugin
      ],
    });

    // Create a new EditorView if:
      // the line numbers we want to color updates
    const view = new EditorView({
      state: startState,
      parent: editorRef.current,
    });

    editorRef.current.view = view;

    // Cleanup function to destroy the editor view when the component unmounts
    return () => {
      view.destroy();
    };
  }, [currentFile.name]); //, currentFile.linesToColor Dependency array to re-initialize the editor if linesToColor change

  // useEffect(() => {
  //   // Handle updating gutter markers when linesToColor changes
  //   const view = editorRef.current?.view;
  //   if (view) {
  //       const decorations = linesToColor.map(line => {
  //           // Create a decoration for each line that should be colored
  //           const pos = view.state.doc.line(line).from;
  //           return Decoration.widget({
  //               widget: new ColoredGutterMarker(colorToUse),
  //               side: -1
  //           }).range(pos);
  //       });

  //       // Dispatch a transaction to update decorations
  //       view.dispatch({

  //       });
  //   }
  // }, [linesToColor, colorToUse]); // React to linesToColor changes
  return <div ref={editorRef} className='editor-div' />;
};

export default Editor;
