import React, { useEffect, useRef } from 'react';
import { EditorState } from '@codemirror/state';
import { EditorView, gutter, GutterMarker, lineNumbers } from '@codemirror/view';
// import { basicSetup } from '@codemirror/basic-setup';

class RedGutterMarker extends GutterMarker {
  toDOM() {
    const marker = document.createElement('div');
    marker.style.background = 'red';
    // marker.textContent = '|'; // Or any marker symbol you prefer
    marker.style.width = '10px'; // Set the width of the block
    marker.style.height = '100%'
    return marker;
  }
}
// Editor component
const EditorComponent = ({ redLines }) => {
  const editorRef = useRef(null);

  useEffect(() => {
    if (!editorRef.current) return;

    // Initialize editor state with custom gutter
    const startState = EditorState.create({
      doc: '// Your code here\n',
      extensions: [
        // basicSetup,
        gutter({
          class: 'custom-gutter',
          lineMarker: (view, line) => {
            // Check if the current line number should have a red marker
            const lineNumber = view.state.doc.lineAt(line.from).number;
            console.log(lineNumber);
            if (redLines.includes(lineNumber)) {
              return new RedGutterMarker();
            }
            return null; // Default markers or no marker
          },
        }),
        lineNumbers(),
      ],
    });

    // Create a new EditorView
    const view = new EditorView({
      state: startState,
      parent: editorRef.current,
    });

    // Cleanup function to destroy the editor view when the component unmounts
    return () => {
      view.destroy();
    };
  }, [redLines]); // Dependency array to re-initialize the editor if redLines change

  return <div ref={editorRef} />;
};

export default EditorComponent;


