// App.js
import React, { useState } from 'react';
import Editor from './components/editor/editor';

function App() {
  const [value, setValue] = useState("Your initial code here...");

  // Define special lines and their custom colors
  const specialLines = [
    { line: 2, color: '#f00' }, // Example: making the 2nd line number red
    // Add more lines and their colors as needed
  ];

  return (
    <div>
      <Editor value={value} specialLines={specialLines} />
    </div>
  );
}

export default App;


