import React, { useRef, useState } from 'react';
import Xarrow from 'react-xarrows';

const connectPointStyle = {
  position: "absolute",
  width: 20,
  height: 20,
  background: "black"
};
const connectPointOffset = {
  right: { left: "100%", top: "50%", transform: "translate(0%, -50%)" },
};

const ConnectPoint = ({ subfolderName, handler, handleClick }) => {
  const textRef = useRef();
  const pointRef = useRef();
  const [position, setPosition] = useState({});
  const [beingDragged, setBeingDragged] = useState(false);

  return (
    <div style={{ display: 'flex', alignItems: 'center', position: 'relative' }}>
      <span
        ref={textRef}
        onClick={(e) => handleClick(e, subfolderName)}
        style={{ marginRight: '10px', cursor: 'pointer' }}
      >
        {subfolderName}
      </span>
      <div
        className="connectPoint"
        style={{
          ...connectPointStyle,
          ...connectPointOffset[handler],
          ...position
        }}
        draggable
        onDragStart={e => {
          setBeingDragged(true);
          e.dataTransfer.setData("arrow", subfolderName);
        }}
        onDrag={e => {
          setPosition({
            position: "fixed",
            left: e.clientX,
            top: e.clientY,
            transform: "none",
            opacity: 0
          });
        }}
        ref={pointRef}
        onDragEnd={e => {
          setPosition({});
          setBeingDragged(false);
        }}
      />
      {beingDragged && <Xarrow start={textRef.current} end={pointRef.current} />}
    </div>
  );
};

export default ConnectPoint;
