import React, { useState } from 'react';
import Editor from './components/editor/editor'; // Make sure the path matches your file structure

function App() {
  const [value, setValue] = useState("console.log('hello world!');\n".repeat(20));

  // Specify the line numbers you want to style differently
  const specialLineNumbers = [3, 5, 7]; // Example line numbers to be styled

  const onChange = (val) => {
    setValue(val);
  };

  return (
    <div className="App">
      <h2>Custom Line Number Editor</h2>
      <Editor value={value} onChange={onChange} specialLineNumbers={specialLineNumbers} />
    </div>
  );
}

export default App;
