import { GutterMarker } from '@codemirror/view';

// Define the SpecialLineMarker class extending GutterMarker for custom line number styling
export class SpecialLineMarker extends GutterMarker {
  constructor(color) {
    super();
    this.color = color; // Store the color for this marker
  }

  // Create the DOM representation of the marker
  toDOM() {
    const span = document.createElement('span');
    span.style.color = this.color; // Apply the stored color
    return span;
  }

  // Optional: Define equality check if needed for performance optimizations
  eq(other) {
    return other instanceof SpecialLineMarker && other.color === this.color;
  }
}
