// App.js
import React, { useState } from 'react';
import Editor from './components/editor/editor';
import './App.css'; // Assuming your CSS for .special-line-number is here

function App() {
  const [value, setValue] = useState("console.log('Hello, world!');\n".repeat(10));

  // Define special lines and their styles
  const specialLines = [
    { line: 2, className: 'special-line-number' }, // Line 2 with a specific class
    // Add more lines and classes as needed
  ];

  return (
    <div className="App">
      <h2>CodeMirror Editor with Custom Line Numbers</h2>
      <Editor value={value} specialLines={specialLines} />
    </div>
  );
}

export default App;



