import React, { useEffect, useState, useRef } from 'react';
import { EditorState } from '@codemirror/state';
import { EditorView, gutter, GutterMarker, lineNumbers } from '@codemirror/view';
import { basicSetup } from '@uiw/codemirror-extensions-basic-setup';
import { javascript } from '@codemirror/lang-javascript';
import { python } from '@codemirror/lang-python';

// Function to determine the language extension based on the file name
const getLanguageExtension = (fileName) => {
  if (fileName.endsWith('.js')) {
    return javascript();
  } else if (fileName.endsWith('.py')) {
    return python();
  }
  return [];
};

class ColoredGutterMarker extends GutterMarker {
  constructor(color) {
    super(); // Call the parent class constructor
    this.color = color; // Store the color value
  }
  toDOM() {
    const marker = document.createElement('div');
    marker.style.background = this.color;
    marker.style.width = '5px'; // Set the width of the block
    marker.style.height = '100%'
    return marker;
  }
}
// Editor component
const Editor = ({ linesToColor, colorToUse, currentFile, onContentChange }) => {
  const editorRef = useRef(null);
  const [editorView, setEditorView] = useState(null);
  console.log("editor file: ", currentFile);
  useEffect(() => {
    if (editorRef.current && !editorView) {
      const updateListener = EditorView.updateListener.of((update) => {
        if (update.docChanged) {
          const newContent = update.state.doc.toString();
          onContentChange(newContent);
        }
      });
      const langExtension = getLanguageExtension(currentFile.name);
      const startState = EditorState.create({
        doc: currentFile.content,
        extensions: [
          basicSetup,
          gutter({
            class: 'custom-gutter',
            lineMarker: (view, line) => {
              const lineNumber = view.state.doc.lineAt(line.from).number;
              if (linesToColor.includes(lineNumber)) {
                return new ColoredGutterMarker(colorToUse);
              }
              return null;
            },
          }),
          lineNumbers(),
          langExtension,
          updateListener
        ],
      });

      const view = new EditorView({
        state: startState,
        parent: editorRef.current,
      });

      setEditorView(view);
      return () => view.destroy();
    }
  }, [linesToColor, colorToUse]); // currentFile, onContentChange, Dependency array to re-initialize the editor if linesToColor change
    // Update the editor's document content when currentFile changes, without re-initializing the editor
  useEffect(() => {
    if (editorView && currentFile) {
      const transaction = editorView.state.update({
        changes: { from: 0, to: editorView.state.doc.length, insert: currentFile.content }
      });
      editorView.dispatch(transaction);
    }
  }, [currentFile.content, editorView]); // Depend on currentFile.content to update content

  return <div ref={editorRef} className='editor-div' />;
};

export default Editor;


