// App.js
import React, { useState } from 'react';
import Editor from './components/editor/editor';

function App() {
  const [value, setValue] = useState("console.log('Hello, world!');\n".repeat(10));

  // Define lines to mark in red
  const linesToMarkInRed = [2, 4]; // Example line numbers

  return (
    <div className="App">
      <h2>CodeMirror Editor with Custom Line Numbers</h2>
      <Editor value={value} linesToMarkInRed={linesToMarkInRed} />
    </div>
  );
}

export default App;





