// App.js
import React, { useState } from 'react';
import Editor from './Editor'; // Adjust the import path as necessary

function App() {
  const [value, setValue] = useState("console.log('hello world!');\n".repeat(10));

  // Define styles for specific lines
  const specialLineStyles = [
    { line: 2, color: '#f00' }, // Line 2 in red
    { line: 4, color: '#00f' }, // Line 4 in blue
    // Add more lines and colors as needed
  ];

  const onChange = (newValue) => {
    setValue(newValue);
  };

  return (
    <div>
      <Editor value={value} onChange={onChange} specialLineStyles={specialLineStyles} />
    </div>
  );
}

export default App;
