import React, { useEffect, useRef } from 'react';
import { EditorState, RangeSetBuilder } from '@codemirror/state';
import { EditorView, gutter, lineNumbers, GutterMarker } from '@codemirror/view';
import { javascript } from '@codemirror/lang-javascript';


class SpecialLineMarker extends GutterMarker {
  constructor(style) {
    super();
    this.style = style;
  }

  toDOM() {
    const span = document.createElement("span");
    Object.assign(span.style, this.style);
    return span;
  }

  eq(other) {
    return other instanceof SpecialLineMarker && other.style === this.style;
  }
}

const Editor = ({ value, specialLines }) => {
  const editorParentRef = useRef(null);

  useEffect(() => {
    if (!editorParentRef.current) return;

    const specialLineMarkers = specialLines.map(({ line, color }) =>
      EditorView.lineMarker({
        line,
        marker: new SpecialLineMarker({color})
      })
    );

    const startState = EditorState.create({
      doc: value,
      extensions: [
        javascript(),
        gutter({
          class: "cm-customLineNumbers",
          markers: () => RangeSetBuilder.from(specialLineMarkers),
        }),
        lineNumbers(),
      ],
    });

    const view = new EditorView({
      state: startState,
      parent: editorParentRef.current,
    });

    return () => {
      view.destroy();
    };
  }, [value, specialLines]);

  return <div ref={editorParentRef} style={{ height: '400px', border: '1px solid black' }} />;
};

export default Editor;



