import React, { useRef } from 'react';

const connectPointStyle = {
  position: "absolute",
  width: 20,
  height: 20,
  background: "black",
  right: 0, // Set right 0 for simplification
  top: "50%",
  transform: "translateY(-50%)"
};

const ConnectPoint = ({ subfolderName, handleClick, boxId, registerRef }) => {
  const pointRef = useRef(null);

  return (
    <div style={{ display: 'flex', alignItems: 'center', position: 'relative' }}>
      <span
        onClick={(e) => handleClick(e, subfolderName)}
        style={{ marginRight: '10px', cursor: 'pointer' }}
      >
        {subfolderName}
      </span>
      <div
        className="connectPoint"
        style={connectPointStyle}
        draggable
        onDragStart={(e) => {
          e.dataTransfer.setData("connectPointId", boxId); // Send boxId for identifying drag start
        }}
        ref={(el) => {
          pointRef.current = el;
          registerRef(boxId, pointRef); // Register ref with parent when available
        }}
      />
    </div>
  );
};

export default ConnectPoint;
