// App.js
import React, { useState } from 'react';
import Editor from './components/editor/editor'; // Adjust the path as necessary

function App() {
  const [value, setValue] = useState("console.log('Hello, World!');\n".repeat(10));

  // Example: Style the 2nd and 4th line numbers differently
  const specialLineNumbers = [2, 4]; // Lines to apply the special styling
  const specialLineColor = "#ff0000"; // Color for the special lines

  return (
    <div>
      <h2>CodeMirror Editor with Custom Line Numbers</h2>
      <Editor
        value={value}
        specialLineNumbers={specialLineNumbers}
        specialLineColor={specialLineColor}
      />
    </div>
  );
}

export default App;
