// Editor.js
import React, { useEffect, useRef } from 'react';
import { EditorState } from '@codemirror/state';
import { EditorView, gutter, lineNumbers } from '@codemirror/view';
import { javascript } from '@codemirror/lang-javascript';
import { RangeSetBuilder } from '@codemirror/rangeset';
import { SpecialLineMarker } from './linemarker'; // Adjust the import path as needed

const Editor = ({ value, specialLines }) => {
  const editorParentRef = useRef(null);

  useEffect(() => {
    if (!editorParentRef.current) return;

    // Function to create markers for special lines
    const createMarkers = (specialLines) => {
      const builder = new RangeSetBuilder();
      specialLines.forEach(({ line, color }) => {
        // Offset line number by 1 because CodeMirror line numbers are zero-based
        const pos = editorView.state.doc.line(line).from;
        builder.add(pos, pos, new SpecialLineMarker(color));
      });
      return builder.finish();
    };

    const startState = EditorState.create({
      doc: value,
      extensions: [
        javascript(),
        lineNumbers(),
        gutter({
          class: "cm-customGutter",
          markers: (view) => createMarkers(specialLines),
          lineMarker: (view, line) => null, // Implement as needed based on specific requirements
        }),
      ],
    });

    const editorView = new EditorView({
      state: startState,
      parent: editorParentRef.current,
    });

    return () => {
      editorView.destroy();
    };
  }, [value, specialLines]);

  return <div ref={editorParentRef} />;
};

export default Editor;




