import React, { useState } from 'react';
import Editor from './components/editor/editor'; // Adjust the path as necessary

function App() {
  const [value, setValue] = useState("console.log('hello world!');\n".repeat(20));

  const specialLineNumbers = [3, 5, 7]; // Lines to be styled differently
  const specialLineColor = "#f00"; // Color for the special lines

  const onChange = (val) => {
    setValue(val);
  };

  return (
    <div className="App">
      <h2>Custom Line Number Editor</h2>
      <Editor 
        value={value} 
        onChange={onChange} 
        specialLineNumbers={specialLineNumbers} 
        specialLineColor={specialLineColor} 
      />
    </div>
  );
}

export default App;
