import React, { useState, useCallback } from 'react';
import Editor from './components/editor/editor';
import './App.css'

function App() {
  const [files] = useState([
    { name: "example.js", content: "// JavaScript content" },
    { name: "example.py", content: "# Python content" },
    // Add more files as needed
  ]);

  // State to keep track of the currently selected file
  const [currentFile, setCurrentFile] = useState(files[0]);

  const handleContentChange = useCallback((newFile) => {
    console.log("in handle content change");
    const fileIndex = files.findIndex(file => file.name === newFile.name);
    files[fileIndex].content = newFile.content;
  }, [currentFile.name]);

  const linesToColor = [2, 3, 4, 5, 6];
  const colorToUse = "red";
  return (
    <div className="App">
      <div className="file-selector">
        {files.map(file => (
          <button key={file.name} onClick={() => setCurrentFile(file)}>
            {file.name}
          </button>
        ))}
      </div>
      <Editor linesToColor={linesToColor} colorToUse={colorToUse} currentFile={currentFile} onContentChange={handleContentChange} />
    </div>
  );
}

export default App;
