import React, { useState, useCallback } from 'react';
import Editor from './components/editor/editor';
import './App.css'

function App() {
  const [files, setFiles] = useState([
    { name: "example.js", content: "// JavaScript content" },
    { name: "example.py", content: "# Python content" },
    // Add more files as needed
  ]);

  // State to keep track of the currently selected file
  const [currentFile, setCurrentFile] = useState(files[0]);

  const handleContentChange = useCallback((newContent) => {
    console.log("in handle content change");
    // console.log("Current file in handle content change: ", currentFile)
    setFiles(files =>
      files.map(file => 
        file.name === currentFile.name ? { name: file.name, content: newContent } : file
      )
    );
  }, [currentFile]);

  // const currentFile = files.find(file => file.name === currentFileName);
  console.log("current file: ", currentFile);

  const linesToColor = [2, 3, 4, 5, 6]; // Example lines to mark with a red gutter
  const colorToUse = "red"
  return (
    <div className="App">
      <div className="file-selector">
        {files.map(file => (
          <button key={file.name} onClick={() => setCurrentFile(file)}>
            {file.name}
          </button>
        ))}
      </div>
      <Editor linesToColor={linesToColor} colorToUse={colorToUse} currentFile={currentFile} onContentChange={handleContentChange} />
    </div>
  );
}

export default App;






