import React, { useState } from 'react';
import Editor from './components/editor/editor';

function App() {
  const [value, setValue] = useState(`function example() {
  console.log('Hello, World!');
}

example();`);

  // Define special lines and their styles
  const specialLines = [
    { line: 2, color: 'red' },
    // More lines and colors as needed
  ];

  return (
    <div>
      <h2>CodeMirror Editor with Custom Line Numbers</h2>
      <Editor value={value} specialLines={specialLines} />
    </div>
  );
}

export default App;
